import { Component } from '@angular/core';
import { Product } from '../product';
import { ActivatedRoute, Router } from '@angular/router';
import { ProductServiceService } from '../product-service.service';

@Component({
  selector: 'app-product-form',
  templateUrl: './product-form.component.html',
  styleUrls: ['./product-form.component.css']
})
export class ProductFormComponent {
  product: Product;
  showError: boolean = false;

  constructor(
    private route: ActivatedRoute, 
    private router: Router, 
    private productService: ProductServiceService
  ) {
    this.product = new Product();
  }

  onSubmit() {
    if (this.product.price === 0 || this.product.quantity === 0) {
      this.showError = true;
      return;
    }
    this.showError = false;
    this.productService.save(this.product).subscribe(result => this.gotoProductList());
  }

  gotoProductList() {
    this.router.navigate(['/products']);
  }
}
<div class="card my-5">
  <div class="card-body">
    <form (ngSubmit)="onSubmit()" #productForm="ngForm">
      <div class="form-group">
        <label for="name">Name</label>
        <input type="text" [(ngModel)]="product.name" 
          class="form-control" 
          id="name" 
          name="name" 
          placeholder="Enter your name"
          required #name="ngModel">
        <div [hidden]="!name.pristine || name.valid" class="alert alert-danger">Name is required</div>
      </div>
      <div class="form-group">
        <label for="description">Description</label>
        <input type="text" [(ngModel)]="product.description" 
          class="form-control" 
          id="description" 
          name="description" 
          placeholder="Enter your description"
          required #description="ngModel">
        <div [hidden]="!description.pristine || description.valid" class="alert alert-danger">Description is required</div>
      </div>
      <div class="form-group">
        <label for="quantity">Quantity</label>
        <input type="number" [(ngModel)]="product.quantity" 
          class="form-control" 
          id="quantity" 
          name="quantity" 
          placeholder="Enter quantity"
          required
          min="1" 
          #quantity="ngModel">
        <div [hidden]="!quantity.pristine || quantity.valid" class="alert alert-danger">
          <span *ngIf="quantity.value < 1">Quantity must be greater than or equal to 1</span>
          Quantity is required
        </div>
        <div *ngIf="showError && (product.quantity === 0 || product.price === 0)" class="alert alert-danger">Price and quantity must be greater than or equal to 1</div>
      </div>
      <div class="form-group">
        <label for="price">Price</label>
        <input type="number" [(ngModel)]="product.price" 
          class="form-control" 
          id="price" 
          name="price" 
          placeholder="Enter price"
          required
          min="1" 
          #price="ngModel">
        <div [hidden]="!price.pristine || price.valid" class="alert alert-danger">
          <span *ngIf="price.value < 1">Price must be greater than or equal to 1</span>
          Price is required
        </div>
      </div>
      <button type="submit" [disabled]="!productForm.form.valid" 
        class="btn btn-info">Submit</button>
    </form>
  </div>
</div>
