import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ProductServiceService } from '../product-service.service';
import { Product } from '../product';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-update-product',
  templateUrl: './update-product.component.html',
  styleUrls: ['./update-product.component.css']
})
export class UpdateProductComponent implements OnInit {
  id!: number;
  product!: Product;
  errors: any = {};

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private productService: ProductServiceService
  ) { }

  ngOnInit() {
    this.product = new Product();
    this.id = this.route.snapshot.params['id'];
    this.getProduct();
  }

  getProduct() {
    this.productService.getProduct(this.id)
      .subscribe(
        (data: Product) => {
          console.log(data);
          this.product = data;
        },
        (error: HttpErrorResponse) => {
          console.log(error);
        }
      );
  }

  onSubmit() {
    this.resetErrors();

    this.productService.updateProduct(this.id, this.product)
      .subscribe(
        (data: Product) => {
          console.log(data);
          this.gotoProductList();
        },
        (error: HttpErrorResponse) => {
          if (error.status === 400) {
            if (error.error.error1) {
              this.errors.name = error.error.error1;
            }
            if (error.error.error2) {
              this.errors.quantity = error.error.error2;
            }
            if (error.error.error3) {
              this.errors.price = error.error.error3;
            }
          } else {
            console.error('Error updating product:', error);
            this.errors.generic = 'An error occurred while updating the product.';
          }
        }
      );
  }

  gotoProductList() {
    this.router.navigate(['/products']);
  }

  resetErrors() {
    this.errors = {};
  }
}
<h2>Update Product</h2>

<div class="form-group">
  <label for="name">Name</label>
  <input type="text" class="form-control" id="name" [(ngModel)]="product.name" name="name" required>
  <div *ngIf="errors.name" class="alert alert-danger">{{ errors.name }}</div>
</div>

<div class="form-group">
  <label for="description">Description</label>
  <input type="text" class="form-control" id="description" [(ngModel)]="product.description" name="description" required>
</div>

<div class="form-group">
  <label for="quantity">Quantity</label>
  <input type="number" class="form-control" id="quantity" [(ngModel)]="product.quantity" name="quantity" required>
  <div *ngIf="errors.quantity" class="alert alert-danger">{{ errors.quantity }}</div>
</div>

<div class="form-group">
  <label for="price">Price</label>
  <input type="number" class="form-control" id="price" [(ngModel)]="product.price" name="price" required>
  <div *ngIf="errors.price" class="alert alert-danger">{{ errors.price }}</div>
</div>

<button type="submit" class="btn btn-primary" (click)="onSubmit()">Update</button>
