import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  uname!: string;
  psw!: string;
  loginError!: string;

  constructor(
    private router: Router,
    private authService: AuthService
  ) { }

  ngOnInit(): void {
  }

  onSubmit() {
    this.authService.login(); // Mettre à jour l'état de connexion en utilisant le service AuthService
    console.log("Logged in successfully!");
    // Redirigez l'utilisateur vers une autre page après une connexion réussie
    this.router.navigate(['/products']);
  }

}
<!DOCTYPE html>
<html>
<head>
  <title>Login Page</title>

</head>
<body>

  <form action="action_page.php" method="post">
    <div class="imgcontainer">
      <img src="img_avatar2.png" alt="Avatar" class="avatar">
    </div>

    <div class="container">
      <label for="uname"><b>Username</b></label>
      <input type="text" placeholder="Enter Username" [(ngModel)]="uname" name="uname" required>

      <label for="psw"><b>Password</b></label>
      <input type="password" placeholder="Enter Password" [(ngModel)]="psw" name="psw" required>

      <button (click)="onSubmit()">Login</button>
    </div>
  </form>

</body>
</html>
