import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ProductServiceService } from '../product-service.service';
import { Product } from '../product';

@Component({
  selector: 'app-update-product',
  templateUrl: './update-product.component.html',
  styleUrls: ['./update-product.component.css']
})
export class UpdateProductComponent implements OnInit {

  id!: number;
  product!: Product;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private productService: ProductServiceService
  ) { }

  ngOnInit() {
    this.product = new Product();
    this.id = this.route.snapshot.params['id'];
    this.getProduct();
  }

  getProduct() {
    this.productService.getProduct(this.id)
      .subscribe(data => {
        console.log(data)
        this.product = data;
      }, error => console.log(error));
  }

  onSubmit() {
    this.productService.updateProduct(this.id, this.product)
      .subscribe(data => {
        console.log(data);
        this.gotoProductList();
      }, error => console.log(error));
  }

  gotoProductList() {
    this.router.navigate(['/products']);
  }
}
<div class="container">
  <div class="row">
    <div class="col-md-12">
      <div class="card bg-dark my-5">
        <div class="card-body">
          <h2 class="card-title text-center text-white py-3">{{ title }}</h2>
          <ul class="text-center list-inline py-3">
            <li class="list-inline-item">
              <a routerLink="/products" class="btn btn-info">List Products</a>
                </li>
            <li class="list-inline-item">
              <a routerLink="/addproduct" class="btn btn-info">Add Product</a>
                </li>
                <li class="list-inline-item">
                  <a routerLink="/login" class="btn btn-info">Login</a>
                    </li>
          </ul>
        </div>
      </div>
      <router-outlet></router-outlet>
    </div>
  </div>
</div>
<h2>Update Product</h2>

<div class="form-group">
  <label for="name">Name</label>
  <input type="text" class="form-control" id="name" [(ngModel)]="product.name" name="name" required>
</div>

<div class="form-group">
  <label for="description">Description</label>
  <input type="text" class="form-control" id="description" [(ngModel)]="product.description" name="description" required>
</div>

<div class="form-group">
  <label for="price">Price</label>
  <input type="number" class="form-control" id="price" [(ngModel)]="product.price" name="price" required>
</div>

<div class="form-group">
  <label for="quantity">Quantity</label>
  <input type="number" class="form-control" id="quantity" [(ngModel)]="product.quantity" name="quantity" required>
</div>

<button type="submit" class="btn btn-primary" (click)="onSubmit()">Update</button>
