import { Component } from '@angular/core';
import { Product } from '../product';
import { ActivatedRoute, Router } from '@angular/router';
import { ProductServiceService } from '../product-service.service';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-product-form',
  templateUrl: './product-form.component.html',
  styleUrls: ['./product-form.component.css']
})
export class ProductFormComponent {
  product: Product;
  nameError = '';
  quantityError = '';
  priceError = '';

  constructor(
    private route: ActivatedRoute, 
    private router: Router, 
    private productService: ProductServiceService
  ) {
    this.product = new Product();
  }

  // Ajoutez la propriété errorMessage dans votre composant
errorMessage: string = '';

// Modifiez la partie de gestion des erreurs dans la méthode onSubmit()
onSubmit(): void {
  this.resetErrors();

  // Appeler le service pour envoyer les données au contrôleur backend
  this.productService['addProduct'](this.product).subscribe(
    (response: any) => {
      // Traiter la réponse du serveur
      console.log('Produit ajouté avec succès :', response);
      this.gotoProductList();
    },
    (errorResponse: any) => {
      // Gérer les réponses d'erreur du serveur
      if (errorResponse.status === 400) {
        const errorText = errorResponse.error;

        // Afficher les messages d'erreur dans le formulaire
        if (errorText.includes('Veuillez saisir toutes les valeurs obligatoires (nom, quantité, prix).')) {
          this.errorMessage = 'Veuillez saisir toutes les valeurs obligatoires (nom, quantité, prix).';
        }
      } else {
        // Gérer les autres erreurs (par exemple, afficher un message d'erreur générique)
        console.error('Erreur lors de l\'ajout du produit :', errorResponse);
        this.errorMessage = 'Une erreur s\'est produite lors de l\'ajout du produit.';
      }
    }
  );
}


  gotoProductList(): void {
  this.router.navigate(['/products']);
}


  resetErrors(): void {
    this.nameError = '';
    this.quantityError = '';
    this.priceError = '';
  }
}
<div class="card my-5">
  <div class="card-body">
    <form (ngSubmit)="onSubmit()" #productForm="ngForm">
      <div class="form-group">
        <label for="name">Name</label>
        <input type="text" [(ngModel)]="product.name" class="form-control" id="name" name="name" placeholder="Enter your name">
        <div *ngIf="errorMessage" class="alert alert-danger">{{ errorMessage }}</div>
      </div>
      <div class="form-group">
        <label for="description">Description</label>
        <input type="text" [(ngModel)]="product.description" class="form-control" id="description" name="description" placeholder="Enter your description">
      </div>
      <div class="form-group">
        <label for="quantity">Quantity</label>
        <input type="number" [(ngModel)]="product.quantity" class="form-control" id="quantity" name="quantity" placeholder="Enter quantity">
        <div *ngIf="errorMessage" class="alert alert-danger">{{ errorMessage }}</div>
      </div>
      <div class="form-group">
        <label for="price">Price</label>
        <input type="number" [(ngModel)]="product.price" class="form-control" id="price" name="price" placeholder="Enter price">
        <div *ngIf="errorMessage" class="alert alert-danger">{{ errorMessage }}</div>
      </div>
      <button type="submit" class="btn btn-info">Submit</button>
    </form>
  </div>
</div>
