import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ProductServiceService } from '../product-service.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

  uname!: string;
  psw!: string;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private productService: ProductServiceService
  ) { }

  onSubmit() {
    this.productService.login(this.uname, this.psw).subscribe(
      response => {
        // Traitez la réponse du serveur après la tentative de connexion réussie
        console.log("Logged in successfully!");
        // Redirigez l'utilisateur vers une autre page ou effectuez d'autres actions nécessaires.
      },
      error => {
        // Traitez les erreurs de connexion
        console.error("Login failed:", error);
      }
    );
  }


  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }



}

  <form action="action_page.php" method="post">
    <div class="imgcontainer">
      <img src="img_avatar2.png" alt="Avatar" class="avatar">
    </div>

    <div class="container">
      <label for="uname"><b>Username</b></label>
      <input type="text" placeholder="Enter Username" [(ngModel)]="uname" name="uname" required>

      <label for="psw"><b>Password</b></label>
      <input type="password" placeholder="Enter Password" [(ngModel)]="psw" name="psw" required>

      <button (click)="onSubmit()">Login</button>
    </div>


  </form>
