import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Product } from './product';

@Injectable({
  providedIn: 'root'
})
export class ProductServiceService {

  private productsUrl: string;
  private loginUrl: string;

  constructor(private http: HttpClient) {
    this.productsUrl = 'http://localhost:8080/products'; // Remplacez par l'URL appropriée pour vos API de produits
    this.loginUrl = 'http://localhost:8080/login'; // Remplacez par l'URL appropriée pour l'API de connexion
  }

  public findAll(): Observable<Product[]> {
    return this.http.get<Product[]>(this.productsUrl);
  }

  public save(product: Product): Observable<Product> {
    return this.http.post<Product>(this.productsUrl, product);
  }

  public getProduct(id: number): Observable<Product> {
    return this.http.get<Product>(`${this.productsUrl}/${id}`);
  }

  public updateProduct(id: number, product: Product): Observable<Product> {
    return this.http.put<Product>(`${this.productsUrl}/${id}`, product);
  }

  public addProduct(product: Product): Observable<Product> {
    return this.http.post<Product>(this.productsUrl, product);
  }

  public login(uname: string, psw: string): Observable<any> {
    const credentials = {
      uname: uname,
      psw: psw
    };
    return this.http.post<any>(this.loginUrl, credentials);
  }

}
