import { Component, OnInit } from '@angular/core';
import { ProductServiceService } from '../product-service.service';
import { Product } from '../product';
import { Router } from '@angular/router';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit {
  products: Product[] = [];
  p = 1; // Page actuelle

  constructor(
    private productService: ProductServiceService,
    private router: Router
  ) {}

  ngOnInit() {
    this.productService.findAll().subscribe(data => {
      this.products = data;
    });
  }

  purchaseProduct(product: Product) {
    // Decrease quantity by 1
    product.quantity -= 1;

    // Check if quantity is zero
    if (product.quantity === 0) {
      // Remove the product from the list
      this.products = this.products.filter(p => p.id !== product.id);
    }
  }

  editProduct(product: Product) {
    // Redirect to the update page with the product ID as a parameter
    this.router.navigate(['/update', product.id]);
  }
}
<div class="card my-5">
  <div class="card-body">
    <table class="table table-bordered table-striped">
      <thead class="thead-dark">
        <tr>
          <th scope="col">#</th>
          <th scope="col">Name</th>
          <th scope="col">Description</th>
          <th scope="col">Quantity</th>
          <th scope="col">Price</th>
          <th scope="col">Action</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let product of products | paginate: { itemsPerPage: 5, currentPage: p }">
          <td>{{ product.id }}</td>
          <td>{{ product.name }}</td>
          <td>{{ product.description }}</td>
          <td>{{ product.quantity }}</td>
          <td>{{ product.price }}</td>
          <td>
            <button class="btn btn-primary" (click)="purchaseProduct(product)">Buy</button>
            <span>&nbsp;</span> <!-- Ajout d'un espace -->
            <button class="btn btn-secondary" (click)="editProduct(product)">Modifier</button>
          </td>
        </tr>
      </tbody>
    </table>
  <!-- Pagination Controls -->
  <pagination-controls (pageChange)="p = $event"></pagination-controls>
</div>
</div>
