import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-authentication',
  templateUrl: './authentication.component.html',
  styleUrls: ['./authentication.component.css']
})
export class AuthenticationComponent implements OnInit {
  login!: string;
  password!: string;

  constructor(private router: Router) { }

  ngOnInit() {
  }

  onSubmit() {
    // Vérifier le login et le mot de passe ici
    if (this.login === 'admin' && this.password === 'password') {
      // Rediriger vers la page de l'application souhaitée
      this.router.navigate(['/dashboard']); // Remplacez '/dashboard' par le lien de l'app approprié
    } else {
      // Afficher un message d'erreur ou effectuer une autre action en cas d'authentification échouée
      console.log('Authentification échouée');
    }
  }
}
<h2>Login</h2>
<form (ngSubmit)="onSubmit()">
  <div>
    <label for="login">Login:</label>
    <input type="text" id="login" name="login" [(ngModel)]="login" required>
  </div>
  <div>
    <label for="password">Password:</label>
    <input type="password" id="password" name="password" [(ngModel)]="password" required>
  </div>
  <button type="submit">Submit</button>
</form>
