import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from './auth.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'Your App Title';
  isLoginPage = false;

  constructor(public authService: AuthService, private router: Router) {}

  ngOnInit() {
    this.router.events.subscribe(event => {
      this.isLoginPage = this.router.url === '/login';
    });
  }
}
<div class="container">
  <div class="row">
    <div class="col-md-12">
      <div class="card bg-dark my-5">
        <div class="card-body">
          <h2 class="card-title text-center text-white py-3">{{ title }}</h2>
          <ul class="text-center list-inline py-3">
            <li class="list-inline-item">
              <a routerLink="/products" class="btn btn-info" *ngIf="authService.isLoggedIn">List Products</a>
            </li>
            <li class="list-inline-item">
              <a routerLink="/addproduct" class="btn btn-info" *ngIf="authService.isLoggedIn">Add Product</a>
            </li>
          </ul>
        </div>
      </div>
      <router-outlet></router-outlet>
      <ng-container *ngIf="isLoginPage">
        <app-login></app-login>
      </ng-container>
    </div>
  </div>
</div>
